import { useState, useEffect } from 'react';
import Button from '../components/Button';
import CardCheckout from '../components/CardCheckout';
import PaymentType from '../components/PaymentType';
// import SmallInput from '../components/SmallInput';
import Policy from '../components/Policy';
import EmailCheckbox from '../components/EmailCheckbox';
import TrustCheckout from '../components/TrustCheckout';
// import LargeInput from '../components/LargeInput';
// import ToastWarning from '../components/ToastWarning';
import axios from 'axios';

import * as yup from 'yup'
import store from '../Stores/storeVehicle';
import { observer } from 'mobx-react';
import { Field, Form, Formik, useFormikContext } from 'formik';

interface Values {
    firstName: string,
    lastName: string,
    email: string,
    ccNumber: string,
    ccName: string,
    cvv: string,
    validade: string,
    cpfCnpj: string,
    cep: string,
    endereco: string,
    numero: string,
    complemento: string,
    cidade: string,
    bairro: string,
    estado: string,       
}

const sellSchema = yup.object().shape({
    firstName: yup.string().required('Seu nome por favor...'),
    lastName: yup.string().required('O sobrenome também!'),
    email: yup.string().email().required('Digite seu email'),
    ccNumber: yup.number().required('Coloque o número do cartão'),
