import { useEffect } from "react";
import { ToastContainer, toast} from 'react-toastify'

type props = { 
    txt: String,
    showToaster: Boolean
}
const Button = ({txt, showToaster}: props) => {
    function showingToaster() {
        if(showToaster)
        return toast.error('ðŸ¦„ Wow so easy!', {
            position: "top-right",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            draggable: true,
            progress: undefined,
            });
    }

    useEffect(() => {
        showingToaster()
    }, [])
    return (
        <div>
            <ToastContainer
            position="bottom-center"
            autoClose={5000}
            hideProgressBar={false}
            newestOnTop={false}
            closeOnClick
            rtl={false}
            pauseOnFocusLoss
            draggable
            pauseOnHover
            />
            <button type="submit"
                    className="border-4
                            border-green-500
                            bg-green-500
                            hover:border-green-200
                            h-12                           
                            rounded-md
                            text-2xl
                            text-white
                            w-56
            ">
                {txt}
            </button>
        </div>
    )
}

export default Button
