import { useState, useEffect } from 'react';
import Header from '../components/layout/Header';
import Sand from '../assets/vehicles/sandcrawler.png'
import Button from '../components/Button';
import CardCheckout from '../components/CardCheckout';
import PaymentType from '../components/PaymentType';
import SmallInput from '../components/SmallInput';
import Policy from '../components/Policy';
import EmailCheckbox from '../components/EmailCheckbox';
import TrustCheckout from '../components/TrustCheckout';
import LargeInput from '../components/LargeInput';

import store from '../Stores/storeVehicle';
import { observer } from 'mobx-react';
import { Field, Form, Formik } from 'formik';

function Checkout() {
    const lgInptStyle = `border-b-2 border-blue-800 text-lg h-12 outline-none px-4 w-80 hover:shadow-xl`
    const smInptStyle = `border-b-2 border-blue-800 text-lg h-12 outline-none px-4 w-36 hover:shadow-xl`
    const [vehicle, setVehicle] = useState(Object)
    const [paymentType, setPaymentType] = useState(String)

    useEffect(() => {
        setVehicle(store.loadVehicle())
    },[])

    return (
        <div>      
            <div className="flex flex-col 
                            justify-center 
                            items-center 
                            w-screen 
                            overflow-x-hidden 
                            pb-12
            ">
                <Formik>
                <Form>
                    <div className="space-y-8 
                                    mt-12
                    ">
                        <div className="space-y-8 
                                        md:space-x-8 
                                        md:flex 
                                        md:flex-row 
                                        md:items-center
                        ">
                            <div>
                                <div className="mt-6 
                                                w-full 
                                                flex 
                                                justify-center
                                ">
                                    <TrustCheckout />  
                                </div>
                                    <CardCheckout img={vehicle.img} 
                                                  alt={vehicle.name} 
                                                  price={vehicle.price} 
                                                  name={vehicle.name} 
                                                  company={vehicle.company} 
                                    />
                            </div>
                            <div className="space-y-8">
                                <div className="mt-6 
                                                w-full 
                                                flex 
                                                justify-center">
                                    <PaymentType />
                                </div>
                                    <Field 
                                                id={'1'}
                                                name={'ccNumber'}
                                                placeholder={'Número do Cartão'}                
                                                typed={'number'}

                                    />
                                        <Field 
                                               id={'2'}
                                               name={'ccName'}
                                               placeholder={'Nome Impresso'}                  
                                               typed={'text'}

                                        />
                                <div className="w-80 flex justify-between">
                                    <Field 
                                           id={'3'}
                                           name={'cvv'}
                                           placeholder={'CVV'}                  
                                           typed={'number'}

                                    />
                                        <Field 
                                               id={'4'}
                                               name={'validade'}
                                               placeholder={'Validade'}                  
                                               typed={'text'}

                                        />
                                </div>
                            </div>
                        </div>
                            <div className="text-2xl 
                                            w-80 
                                            md:w-full 
                                            text-center 
                                            text-green-500
                            ">
                                <h1>
                                Preencha seus dados para adquirir o seu veículo:
                                </h1>
                            </div>
                        <div className="space-y-8 
                                        md:space-y-0 
                                        md:space-x-8 
                                        md:flex 
                                        md:flex-row
                        ">
                            <div className="space-y-8">
                                <Field 
                                       id={'5'}
                                       name={'firstName'}
                                       placeholder={'Nome'}                
                                       typed={'text'}

                                />
                                <Field 
                                       id={'6'}
                                       name={'secondName'}
                                       placeholder={'Sobre Nome'}
                                       typed={'text'}

                                />
                                    <Field 
                                           id={'7'}
                                           name={'email'}
                                           placeholder={'Email'}
                                           typed={'text'}

                                    />
                                <Field 
                                       id={'8'}
                                       name={'cpfCnpj'}
                                       placeholder={'CPF/CNPJ'}
                                       typed={'text'}

                                />
                                <Field 
                                       id={'9'}
                                       name={'cep'}
                                       placeholder={'CEP'}
                                       typed={'number'}

                                />
                            </div>
                                <div className="space-y-8">
                                    <Field 
                                           id={'10'}
                                           name={'endereco'}
                                           placeholder={'Endereço'}
                                           typed={'text'}

                                    />
                                    <div className="w-80 flex justify-between">
                                        <Field 
                                               id={'11'}
                                               name={'numero'}
                                               placeholder={'Número'}                  
                                               typed={'number'}

                                        />
                                            <Field 
                                                   id={'12'}
                                                   name={'complemento'}
                                                   placeholder={'Complemento'}                  
                                                   typed={'text'}

                                            />
                                    </div>
                                        <Field 
                                               id={'13'}
                                               name={'cidade'}
                                               placeholder={'Cidade'}
                                               typed={'text'}

                                        />
                                    <Field 
                                           id={'14'}
                                           name={'bairro'}
                                           placeholder={'Bairro'}
                                           typed={'text'}

                                    />
                                        <Field 
                                               id={'15'}
                                               name={'estado'}
                                               placeholder={'Estado'}
                                               typed={'text'}

                                        />
                                </div>
                        </div>
                        <div className="md:flex md:space-x-8">
                            <Policy />
                            <EmailCheckbox />
                        </div>
                        <div className="w-full 
                                        flex 
                                        justify-center"
                        >
                            <Button  txt={'COMPRAR'} />
                        </div>
                    </div>
                </Form>
                </Formik>
            </div>
        </div>
  );
}

export default observer(Checkout);
