import { useState, useEffect } from 'react';
import Header from '../components/layout/Header';
import Sand from '../assets/vehicles/sandcrawler.png'
import Button from '../components/Button';
import CardCheckout from '../components/CardCheckout';
import PaymentType from '../components/PaymentType';
// import SmallInput from '../components/SmallInput';
import Policy from '../components/Policy';
import EmailCheckbox from '../components/EmailCheckbox';
import TrustCheckout from '../components/TrustCheckout';
// import LargeInput from '../components/LargeInput';

import * as yup from 'yup'
import store from '../Stores/storeVehicle';
import { observer } from 'mobx-react';
import { Field, Form, Formik, FormikProps, useFormik } from 'formik';

interface Values {
    firstName: string,
    lastName: string,
    email: string,
    ccNumber: string,
    ccName: string,
    cvv: string,
    validade: string,
    cpfCnpj: string,
    cep: string,
    endereco: string,
    numero: string,
    complemento: string,
    cidade: string,
    bairro: string,
    estado: string,       
}

const Checkout: React.FunctionComponent = () => {
    const lgInptStyle = `flex flex-col border-b-2 text-lg h-12 outline-none px-4 w-80 hover:shadow-xl`
    const smInptStyle = `border-b-2 text-lg h-12 outline-none px-4 w-36 hover:shadow-xl`
    const [vehicle, setVehicle] = useState(Object)
    const [paymentType, setPaymentType] = useState(String)
    const [displayFormStatus, setDisplayFormStatus] = useState(false)
    const [formStatus, setFormStatus] = useState<IFormStatus>({
        message: '',
        type: '',
    })

    const sellSchema = yup.object().shape({
        firstName: yup.string().required('Seu nome por favor...'),
        lastName: yup.string().required('O sobrenome também!'),
        email: yup.string().email().required('Digite seu email'),
        ccNumber: yup.number().required('Coloque o número do cartão'),
        ccName: yup.string().required('Digite o nome que está no cartão'),
        cvv: yup.string().required('Lembre-se do cvv!, atrás do cartão!'),
        validade: yup.string().required('Digite o ano de validade'),
        cpfCnpj: yup.string().required('Digite o seu CPF/CNJP'),
