import { faExclamationTriangle } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useEffect } from "react";
import { ToastContainer, toast} from 'react-toastify'

type props = { 
    txt: String,
    showToaster: Boolean
}
const Button = ({txt, showToaster}: props) => {
    function showingToaster() {
        if(!showToaster)
        return toast.error('ðŸ¦„ Wow so easy!', {
            position: "top-right",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            draggable: true,
            progress: undefined,
            });
    }

    return (
        <div>
            <ToastContainer
            position="bottom-center"
            autoClose={5000}
            hideProgressBar={false}
            newestOnTop={false}
            closeOnClick
            rtl={false}
            pauseOnFocusLoss
            draggable
            pauseOnHover
            >
                <div className="flex 
                    flex-col 
                    justify-center 
                    items-center 
                    text-3xl 
                    text-center 
                    text-white 
                    shadow-xl 
                    w-80 h-auto 
                    py-4 
                    px-4
                    rounded-lg 
                    bg-red-600
                    relative
                ">
                    {/* <FontAwesomeIcon icon={faTimes}
                                    className="absolute top-2 right-2 text-sm cursor-pointer text-white
                    "/>         */}
                    <p>Confira todos os dados</p>
                    <div className="space-x-4 
                                    mt-2">
                        <FontAwesomeIcon icon={faExclamationTriangle} 
                                        className="text-yellow-400
                        "/>
                        <FontAwesomeIcon icon={faExclamationTriangle} 
                                        className="text-yellow-400
                        "/>
                        <FontAwesomeIcon icon={faExclamationTriangle} 
                                        className="text-yellow-400
                        "/>
                    </div>
                </div>
            </ToastContainer>
            <button onClick={showingToaster}
                    type="submit"
                    className="border-4
                            border-green-500
                            bg-green-500
                            hover:border-green-200
                            h-12                           
                            rounded-md
                            text-2xl
                            text-white
                            w-56
            ">
                {txt}
            </button>
        </div>
    )
}

export default Button
