import { useState, useEffect } from 'react';
import Header from '../components/layout/Header';
import Sand from '../assets/vehicles/sandcrawler.png'
import Button from '../components/Button';
import CardCheckout from '../components/CardCheckout';
import PaymentType from '../components/PaymentType';
// import SmallInput from '../components/SmallInput';
import Policy from '../components/Policy';
import EmailCheckbox from '../components/EmailCheckbox';
import TrustCheckout from '../components/TrustCheckout';
// import LargeInput from '../components/LargeInput';

import * as yup from 'yup'
import store from '../Stores/storeVehicle';
import { observer } from 'mobx-react';
import { Field, Form, Formik, FormikHelpers } from 'formik';

interface Values {
    firstName: string,
    lastName: string,
    email: string,
    ccNumber: string,
    ccName: string,
    cvv: string,
    validade: string,
    cpfCnpj: string,
    cep: string,
    endereco: string,
    numero: string,
    complemento: string,
    cidade: string,
    bairro: string,
    estado: string,       
}


function Checkout() {
    const lgInptStyle = `flex flex-col border-b-2 border-blue-800 text-lg h-12 outline-none px-4 w-80 hover:shadow-xl`
    const smInptStyle = `border-b-2 border-blue-800 text-lg h-12 outline-none px-4 w-36 hover:shadow-xl`
    const [vehicle, setVehicle] = useState(Object)
    const [paymentType, setPaymentType] = useState(String)

    const sellSchema = yup.object().shape({
        firstName: yup.string().required(),
        lastName: yup.string().required(),
        email: yup.string().required(),
        ccNumber: yup.string().required(),
        ccName: yup.string().required(),
        cvv: yup.string().required(),
        validade: yup.string().required(),
        cpfCnpj: yup.string().required(),
        cep: yup.string().required(),
        endereco: yup.string().required(),
        numero: yup.string().required(),
        complemento: yup.string().required(),
        cidade: yup.string().required(),
        bairro: yup.string().required(),
        estado: yup.string().required(), 
    })

    const initialValues = {
        firstName: '',
        lastName: '',
        email: '',
        ccNumber: '',
        ccName: '',
        cvv: '',
        validade: '',
        cpfCnpj: '',
        cep: '',
        endereco: '',
        numero: '',
        complemento: '',
        cidade: '',
        bairro: '',
        estado: '',  
    }
    useEffect(() => {
        setVehicle(store.loadVehicle())
    },[])

    return (
        <div>      
            <div className="flex flex-col 
                            justify-center 
                            items-center 
                            w-screen 
                            overflow-x-hidden 
                            pb-12
            ">
                <Formik initialValues={initialValues}
                        validationSchema={sellSchema}
                        onSubmit={(
                            values: Values,
                            { setSubmitting }: FormikHelpers<Values>
                          ) => {
                            setTimeout(() => {
                              alert(JSON.stringify(values, null, 2));
                              setSubmitting(false);
                            }, 500);
                          }}>
                <Form>
                    <div className="space-y-8 
                                    mt-12
                    ">
                        <div className="space-y-8 
                                        md:space-x-8 
                                        md:flex 
                                        md:flex-row 
                                        md:items-center
                        ">
                            <div>
                                <div className="mt-6 
                                                w-full 
                                                flex 
                                                justify-center
                                ">
                                    <TrustCheckout />  
                                </div>
                                    <CardCheckout img={vehicle.img} 
                                                  alt={vehicle.name} 
                                                  price={vehicle.price} 
                                                  name={vehicle.name} 
                                                  company={vehicle.company} 
                                    />
                            </div>
                            <div className="space-y-8">
                                <div className="mt-6 
                                                w-full 
                                                flex 
                                                justify-center">
                                    <PaymentType />
                                </div>

                                        <Field className={lgInptStyle} 
                                            id={'ccNumber'}
                                            name={'ccNumber'}
                                            placeholder={'Número do Cartão'}                
                                            typed={'number'}

                                        />
                                            <Field className={lgInptStyle}
                                                id={'ccName'}
                                                name={'ccName'}
                                                placeholder={'Nome Impresso'}                  
                                                typed={'text'}

                                            />
                                <div className="w-80 flex justify-between">
                                    <Field className={smInptStyle}
                                           id={'cvv'}
                                           name={'cvv'}
                                           placeholder={'CVV'}                  
                                           typed={'number'}

                                    />
                                        <Field className={smInptStyle}
                                               id={'validade'}
                                               name={'validade'}
                                               placeholder={'Validade'}                  
                                               typed={'text'}

                                        />
                                </div>
                            </div>
                        </div>
                            <div className="text-2xl 
                                            w-80 
                                            md:w-full 
                                            text-center 
                                            text-green-500
                            ">
                                <h1>
                                Preencha seus dados para adquirir o seu veículo:
                                </h1>
                            </div>
                        <div className="space-y-8 
                                        md:space-y-0 
                                        md:space-x-8 
                                        md:flex 
                                        md:flex-row
                        ">
                            <div className="space-y-8">
                                <Field className={lgInptStyle}
                                       id={'firstName'}
                                       name={'firstName'}
                                       placeholder={'Nome'}                
                                       typed={'text'}

                                />
                                <Field className={lgInptStyle}
                                       id={'lastName'}
                                       name={'lastName'}
                                       placeholder={'Sobre Nome'}
                                       typed={'text'}

                                />
                                    <Field className={lgInptStyle}
                                           id={'email'}
                                           name={'email'}
                                           placeholder={'Email'}
                                           typed={'text'}

                                    />
                                <Field className={lgInptStyle}
                                       id={'cpfCnpj'}
                                       name={'cpfCnpj'}
                                       placeholder={'CPF/CNPJ'}
                                       typed={'text'}

                                />
                                <Field className={lgInptStyle}
                                       id={'cep'}
                                       name={'cep'}
                                       placeholder={'CEP'}
                                       typed={'number'}

                                />
                            </div>
                                <div className="space-y-8">
                                    <Field className={lgInptStyle}
                                           id={'endereco'}
                                           name={'endereco'}
                                           placeholder={'Endereço'}
                                           typed={'text'}
                                           disabled={true}

                                    />
                                    <div className="w-80 flex justify-between">
                                        <Field className={smInptStyle}
                                               id={'numero'}
                                               name={'numero'}
                                               placeholder={'Número'}                  
                                               typed={'text'}
                                               disabled={true}

                                        />
                                            <Field className={smInptStyle}
                                                   id={'complemento'}
                                                   name={'complemento'}
                                                   placeholder={'Complemento'}                  
                                                   typed={'text'}
                                                   disabled={true}

                                            />
                                    </div>
                                        <Field className={lgInptStyle}
                                               id={'cidade'}
                                               name={'cidade'}
                                               placeholder={'Cidade'}
                                               typed={'text'}
                                               disabled={true}

                                        />
                                    <Field className={lgInptStyle}
                                           id={'bairro'}
                                           name={'bairro'}
                                           placeholder={'Bairro'}
                                           typed={'text'}
                                           disabled={true}

                                    />
                                        <Field className={lgInptStyle}
                                               id={'estado'}
                                               name={'estado'}
                                               placeholder={'Estado'}
                                               typed={'text'}
                                               disabled={true}

                                        />
                                </div>
                        </div>
                        <div className="md:flex md:space-x-8">
                            <Policy />
                            <EmailCheckbox />
                        </div>
                        <div className="w-full 
                                        flex 
                                        justify-center"
                        >
                            <Button  txt={'COMPRAR'} />
                        </div>
                    </div>
                </Form>
                </Formik>
            </div>
        </div>
  );
}

export default observer(Checkout);
