import { useLocation } from 'react-router';
import { AnimatePresence, motion } from "framer-motion";
import { 
    Route, 
    Switch,
} from 'react-router-dom';

import LoadPage from './views/LoadPage';
import Home from './views/Home';
import Checkout from './views/Checkout';
import Vehicles from './views/Vehicles';
import GoodBuy from './views/GoodBuy';
import Header from './components/layout/Header';


const Routes = () => {
    const location = useLocation()

    const pageVariants = {
        initial: {
            opacity: 0,
            scale: 1
        },
        in: {
            opacity: 1,
            scale: 1
        },
        out: {
            opacity: 0,
            scale: 1
        }
    };
    const pageTransition = {
        type: "tween",
        ease: "anticipate",
        duration: 0.4
    };

    return (
        <AnimatePresence exitBeforeEnter initial={false}>
            <Switch>

                    <Route path={['/', '/loadPage', 'vehicles', 'checkout', 'sucess']}>
                        { location.pathname !== '/loadpage' && (
                                <div>
                                    <Header /> 
                                    <div className="h-24"></div>
                                </div>
                            )
                        }
                        <Switch location={location} key={location.pathname}>

                            <motion.div
                                initial="initial"
                                animate="in"
                                exit="out"
                                variants={pageVariants}
                                transition={pageTransition}
                            >
                                <Route path="/loadPage" component={LoadPage} />
                                <Route path="/vehicles" component={Vehicles} />                        
                                <Route path="/checkout" component={Checkout} />
                                <Route path="/success" component={GoodBuy} />
                                <Route path="/" component={Home} />
                            </motion.div>
                        </Switch>
                    </Route>
            </Switch>
        </AnimatePresence>
    )

}

export default Routes
